mapper.select:
select  from customer_network
where 1 = 1 and issue = #{issue}

        AND create_time  >=  #{strtime}


        AND create_time  <=  #{endtime}



        and (corporate_name like #{content} or corporate_phone like #{content})

ORDER BY id DESC

select id,track_id,source,last_follow_name,last_follow_result,sign,proposer,create_time,issue,receiving_time from customer_network
where  issue = 1 and `status` = 0

  AND create_time  >=  #{strtime}


  AND create_time  <=  #{endtime}



  and (corporate_name like #{content} or corporate_phone like #{content})

ORDER BY id DESC

SELECT * from (SELECT id,track_id,corporate_name,last_follow_name,last_follow_result,leader_name,activation,proposer,DATE_FORMAT(create_time,'%Y-%m-%d') as create_time,sign,source,TIMESTAMPDIFF(second ,SYSDATE(),overdue_time) as overdue_time,issue,leader_sign from customer_network WHERE
`status` in (7,10) UNION all SELECT id,track_id,corporate_name,last_follow_name,last_follow_result,leader_name,activation,proposer,DATE_FORMAT(create_time,'%Y-%m-%d') as create_time,sign,source,TIMESTAMPDIFF(second ,SYSDATE(),overdue_time) as overdue_time,issue,leader_sign FROM customer_telemarke WHERE `status` in (7,10) )
as t  WHERE last_follow_name = #{lastFollowName} and activation = 0 and issue = #{issue}

  AND create_time  >=  #{strtime}


  AND create_time  <=  #{endtime}



  and (corporate_name like #{content} or corporate_phone like #{content})

ORDER BY create_time DESC

SELECT * from (SELECT id,track_id,corporate_name,last_follow_name,last_follow_result,leader_name,activation,proposer,DATE_FORMAT(create_time,'%Y-%m-%d') as create_time,sign,source,TIMESTAMPDIFF(second ,SYSDATE(),overdue_time) as overdue_time,issue,`status` from customer_network WHERE `status` in(5,10) UNION all SELECT id,track_id,corporate_name,last_follow_name,last_follow_result,leader_name,activation,proposer,DATE_FORMAT(create_time,'%Y-%m-%d') as create_time,sign,source,TIMESTAMPDIFF(second ,SYSDATE(),overdue_time) as overdue_time,issue,`status` FROM customer_telemarke WHERE `status` in(5,10) ) as t WHERE ((leader_name = #{lastFollowName} and status = 5) or last_follow_name = #{lastFollowName}) and activation = 0 and issue = 1
AND create_time >= #{strtime}
AND create_time <= #{endtime}
and (corporate_name like #{content} or corporate_phone like #{content})
ORDER BY create_time DESC

select  from customer_network
where  `status` = 7

  AND create_time  >=  #{strtime}


  AND create_time  <=  #{endtime}



  and (corporate_name like #{content} or corporate_phone like #{content})

ORDER BY id DESC

select id,track_id,corporate_name,corporate_phone,last_follow_name,last_follow_time,TIMESTAMPDIFF(second ,SYSDATE(),overdue_time) as overdue_time,leader_sign,last_follow_result,total,grade,activation_time from customer_network
where  (last_follow_name = #{lastFollowName} and status = 10 and activation = 1 or  (leader_name = #{lastFollowName} and status = 5)

  AND create_time  >=  #{strtime}


  AND create_time  <=  #{endtime}



  and (corporate_name like #{content} or corporate_phone like #{content})

ORDER BY id DESC

