mapper.select:
select id,track_id,name,corporate_name,corporate_phone,registered_capital,establish_time,operation,issue,state,create_time,last_follow_name, source,last_follow_time,city,other_phone,address from customer_telemarke where issue in(0,1) and `status`in (0,1,2)
AND create_time >= #{strtime}
AND create_time <= #{endtime}
and (corporate_name like #{content} or corporate_phone like #{content})
ORDER BY id DESC

select id,track_id,name,corporate_name,corporate_phone,registered_capital,establish_time,operation,issue,state,create_time,last_follow_name, source,last_follow_time,city,other_phone,address from customer_telemarke where issue = 1 and `status`in (0,1,2)
AND create_time >= #{strtime}
AND create_time <= #{endtime}
and (corporate_name like #{content} or corporate_phone like #{content})
ORDER BY id DESC

select id,track_id,name,corporate_name,corporate_phone,registered_capital,establish_time,operation,issue,state,create_time,last_follow_name, source,last_follow_time,city,other_phone,address from customer_telemarke where 1 = 1 and issue = 1 and appoint = 0
AND create_time >= #{strtime}
AND create_time <= #{endtime}
and (corporate_name like #{content} or corporate_phone like #{content})
ORDER BY id DESC

select id,track_id,name,corporate_name,corporate_phone,registered_capital,establish_time,operation,issue,state,create_time,last_follow_name, source,last_follow_time,city,other_phone,address,status from customer_telemarke where 1 = 1 and (last_follow_name = #{lastFollowName} or leader_name = #{lastFollowName})
AND create_time >= #{strtime}
AND create_time <= #{endtime}
and (corporate_name like #{content} or corporate_phone like #{content})
ORDER BY id DESC

select id,track_id,name,corporate_name,corporate_phone,registered_capital,establish_time,operation,issue,state,status,create_time,last_follow_name, source,last_follow_time,city,other_phone,address from customer_telemarke where leader_name = #{lastFollowName} and `status` = 5
AND create_time >= #{strtime}
AND create_time <= #{endtime}
and (corporate_name like #{content} or corporate_phone like #{content})
ORDER BY id DESC

select id,name,corporate_name,corporate_phone,create_time,source,last_follow_name,track_id,order_state from customer_telemarke
where 1 = 1 and status in(3,4)


  AND create_time  >=  #{strtime}


  AND create_time  <=  #{endtime}



  and (corporate_name like #{content} or corporate_phone like #{content})

ORDER BY id DESC

select id,track_id,name,corporate_name,corporate_phone,registered_capital,establish_time,operation,issue,state,status,create_time,last_follow_name,
source,last_follow_time,city,other_phone,address,TIMESTAMPDIFF(second ,SYSDATE(),overdue_time) as overdue_time from customer_telemarke
where status = #{status} and last_follow_name = #{lastFollowName}

update.foreach:
